import { Component, OnInit } from '@angular/core';
import { ApiService } from '../service/api.service';

@Component({
  selector: 'app-movies',
  templateUrl: './movies.component.html',
  styleUrls: ['./movies.component.css']
})
export class MoviesComponent implements OnInit {

  data: any[] = []; // Inicializa data como un array vacío
  movieTitle: string = ''; 

  constructor(private apiService: ApiService) { }

  ngOnInit(): void {
    // No es necesario llamar a llenardata() aquí ya que se invoca en ngOnInit()
  }

  buscarPelicula() {
    this.apiService.getData(this.movieTitle).subscribe(data => {
      this.data = data?.Search || []; // Utiliza el operador '?' para manejar valores potencialmente nulos o indefinidos
      console.log(this.data);
    });
  }
}
