  providedIn: 'root'
})
export class ApiService {

  private urlApi='http://www.omdbapi.com/?apikey=a145b62f&i=tt1285016';

  constructor(private http: HttpClient) { }

  public getdata(title:string):Observable<any>{
    const url= 'http://www.omdbapi.com/?apikey=a145b62f&t=The%20Dark%20Knight'
    return this.http.get<any>(this.urlApi);
